import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../AnswerContext'; // ✅ Make sure this matches your real path
import '../styles/MoodSelectionPage.css';

function MoodSelectionPage() {
    const navigate = useNavigate();
    const { answers, setAnswers } = useContext(AnswerContext);

    const handleMoodSelect = (mood) => {
        const updatedAnswers = {
            ...answers,
            Q1: mood === 'comedy' ? 1 : 0
        };

        console.log("✅ Setting Q1 answer in context:", updatedAnswers);
        setAnswers(updatedAnswers);

        // ✅ Slight delay to allow context to update before navigation
        setTimeout(() => {
            if (mood === 'comedy') {
                navigate('/musical');
            } else {
                navigate('/drama'); // update if you have a different path for serious mood
            }
        }, 50); // 50ms delay is usually enough
    };

    return (
        <div>
            <h1>What kind of movie are you in the mood for?</h1>
            <button onClick={() => handleMoodSelect('comedy')}>Lighthearted and Comedic</button>
            <button onClick={() => handleMoodSelect('drama')}>Serious and Dramatic</button>
        </div>
    );
}

export default MoodSelectionPage;
