import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const RomanceRecommendation = () => {
    const { answers } = useContext(AnswerContext);
    const navigate = useNavigate();
    const [movie, setMovie] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchRecommendation = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/recommend', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ answers, filters: {} }),
                });

                const data = await response.json();

                if (data.recommendation) {
                    setMovie(data.recommendation);
                } else {
                    setError(data.message || 'No recommendation found.');
                }
            } catch (err) {
                setError('Error fetching recommendation.');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchRecommendation();
    }, [answers]);

    if (loading) {
        return <div style={styles.container}><h2>Loading recommendation...</h2></div>;
    }

    if (error) {
        return (
            <div style={styles.container}>
                <h2>{error}</h2>
                <button onClick={() => navigate('/')}>Go Back</button>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h1>ðŸŽ¬ Your Romantic Movie Recommendation</h1>
            <h2>{movie.title} ({movie.year})</h2>
            <p><strong>Rating:</strong> {movie.rating}</p>
            <p><strong>Duration:</strong> {movie.duration} minutes</p>
            <p><strong>Language:</strong> {movie.language}</p>
            <p><strong>Description:</strong> {movie.description}</p>
            {movie.poster && (
                <img
                    src={movie.poster}
                    alt={`${movie.title} poster`}
                    style={styles.poster}
                />
            )}
            <button onClick={() => navigate('/')} style={styles.button}>
                Choose Another Movie
            </button>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        padding: '2rem',
    },
    poster: {
        marginTop: '1rem',
        maxWidth: '300px',
        borderRadius: '8px',
    },
    button: {
        marginTop: '2rem',
        padding: '0.75rem 1.5rem',
        fontSize: '1rem',
        cursor: 'pointer',
    },
};

export default RomanceRecommendation;
