import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const MusicalRecommendation = () => {
    const { answers, filters } = useContext(AnswerContext);
    const navigate = useNavigate();
    const [movie, setMovie] = useState(null);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchRecommendation = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/recommend', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ answers, filters })
                });

                const data = await response.json();

                if (data.movie) {
                    setMovie(data.movie);
                } else {
                    setError(data.message || 'No recommendation available.');
                }
            } catch (err) {
                console.error('Error fetching recommendation:', err);
                setError('Error fetching recommendation.');
            }
        };

        fetchRecommendation();
    }, [answers, filters]);

    if (error) {
        return (
            <div style={styles.container}>
                <h2>{error}</h2>
                <button onClick={() => navigate('/')}>Go back</button>
            </div>
        );
    }

    if (!movie) {
        return <div style={styles.container}><p>Loading...</p></div>;
    }

    return (
        <div style={styles.container}>
            <h1>ðŸŽ¬ Your Musical Movie Recommendation</h1>
            <h2>{movie.title} ({movie.year})</h2>
            <p><strong>Rating:</strong> {movie.rating}</p>
            <p><strong>Duration:</strong> {movie.duration} minutes</p>
            <p><strong>Language:</strong> {movie.language}</p>
            <p><strong>Description:</strong> {movie.description}</p>
            {movie.poster && (
                <img
                    src={movie.poster}
                    alt={`${movie.title} poster`}
                    style={styles.poster}
                />
            )}
            <button onClick={() => navigate('/')} style={styles.button}>Choose Another Movie</button>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        padding: '2rem',
    },
    poster: {
        marginTop: '1rem',
        maxWidth: '300px',
        borderRadius: '8px',
    },
    button: {
        marginTop: '2rem',
        padding: '0.75rem 1.5rem',
        fontSize: '1rem',
        cursor: 'pointer',
    },
};

export default MusicalRecommendation;
