import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const HorrorRecommendation = () => {
    const { answers, filters, resetAnswers, resetFilters } = useContext(AnswerContext);
    const navigate = useNavigate();
    const [movie, setMovie] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        console.log("HorrorRecommendation: useEffect triggered");
        console.log("Current answers:", answers);
        console.log("Current filters:", filters);

        const fetchRecommendation = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/recommend', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ answers, filters })
                });

                const data = await response.json();
                console.log("Horror - Data received from backend:", data);

                if (data.movie) {
                    setMovie(data.movie);
                } else {
                    setError(data.message || 'No recommendation found.');
                }
            } catch (err) {
                console.error("Horror - Error during fetch:", err);
                setError("Error fetching recommendation.");
            } finally {
                setLoading(false);
            }
        };

        fetchRecommendation();
    }, [answers, filters]);

    const handleResetAndGoHome = () => {
        resetAnswers();
        resetFilters();
        navigate('/');
    };

    if (loading) {
        return <div style={styles.container}><p>Loading recommendation...</p></div>;
    }

    if (error) {
        return (
            <div style={styles.container}>
                <h2>{error}</h2>
                <button onClick={handleResetAndGoHome}>Go Back</button>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h1>ðŸŽ¬ Your Horror Movie Recommendation</h1>
            <h2>{movie.title} ({movie.year})</h2>
            <p><strong>Rating:</strong> {movie.rating}</p>
            <p><strong>Duration:</strong> {movie.duration} minutes</p>
            <p><strong>Language:</strong> {movie.language}</p>
            <p><strong>Description:</strong> {movie.description}</p>
            {movie.poster && <img src={movie.poster} alt={`${movie.title} poster`} style={styles.poster} />}
            <button onClick={handleResetAndGoHome} style={styles.button}>Choose Another Movie</button>
        </div>
    );
};

const styles = {
    container: { textAlign: 'center', padding: '2rem' },
    poster: { marginTop: '1rem', maxWidth: '300px', borderRadius: '8px' },
    button: { marginTop: '2rem', padding: '0.75rem 1.5rem', fontSize: '1rem', cursor: 'pointer' }
};

export default HorrorRecommendation;
