import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const MusicalRecommendation = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const movie = location.state?.movie;

    if (!movie) {
        return (
            <div style={styles.container}>
                <h2>No Musical Recommendation Found</h2>
                <p>Try adjusting your answers or filters.</p>
                <button style={styles.button} onClick={() => navigate('/')}>Go Back Home</button>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h2>ðŸŽµ Your Musical Movie Recommendation</h2>
            <div style={styles.card}>
                <h3>{movie.title} ({movie.year})</h3>
                <p><strong>Rating:</strong> {movie.rating}</p>
                <p><strong>Duration:</strong> {movie.duration} minutes</p>
                <p><strong>Language:</strong> {movie.language}</p>
                <p><strong>Description:</strong> {movie.description}</p>
                {movie.poster && (
                    <img src={movie.poster} alt={`${movie.title} Poster`} style={styles.poster} />
                )}
            </div>
            <button style={styles.button} onClick={() => navigate('/')}>Start Over</button>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        padding: '2rem',
    },
    card: {
        border: '1px solid #ccc',
        padding: '1rem',
        marginTop: '1rem',
        maxWidth: '400px',
        margin: 'auto',
        borderRadius: '10px',
        backgroundColor: '#f5f5f5'
    },
    poster: {
        width: '100%',
        maxWidth: '200px',
        marginTop: '1rem'
    },
    button: {
        marginTop: '2rem',
        padding: '0.75rem 1.5rem',
        fontSize: '1rem',
        cursor: 'pointer'
    }
};

export default MusicalRecommendation;
