import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const AnimatedQuestions = () => {
    const { answers, setAnswers } = useContext(AnswerContext);
    const navigate = useNavigate();

    const handleAnswer = (value) => {
        const updatedAnswers = { ...answers, Q3: value };
        setAnswers(updatedAnswers);

        if (value === 1) {
            // Likes animated → go to animation recommendation
            navigate('/recommend/animation');
        } else {
            // Doesn't like animated → next question
            navigate('/romance');
        }
    };

    return (
        <div style={styles.container}>
            <h2>Do you enjoy movies that are animated?</h2>
            <div style={styles.buttonContainer}>
                <button onClick={() => handleAnswer(1)} style={styles.button}>Yes</button>
                <button onClick={() => handleAnswer(0)} style={styles.button}>No</button>
            </div>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '2rem',
        padding: '1rem'
    },
    buttonContainer: {
        marginTop: '1.5rem',
        display: 'flex',
        justifyContent: 'center',
        gap: '1rem'
    },
    button: {
        padding: '0.75rem 1.5rem',
        fontSize: '1.1rem',
        cursor: 'pointer'
    }
};

export default AnimatedQuestions;
