import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../AnswerContext'; // adjust if needed
import '../styles/MoodSelectionPage.css';

function MoodSelectionPage() {
    const navigate = useNavigate();
    const { answers, setAnswers } = useContext(AnswerContext);

    const handleMoodSelect = (mood) => {
        // Save Q1 value based on mood
        const updatedAnswers = {
            ...answers,
            Q1: mood === 'comedy' ? 1 : 0
        };
        setAnswers(updatedAnswers);

        if (mood === 'comedy') {
            navigate('/musical');
        } else if (mood === 'drama') {
            // navigate('/drama'); // if implemented later
        }
    };

    const backgroundStyle = {
        height: '100vh',
        backgroundImage: `url(${process.env.PUBLIC_URL + '/images/drama.jpg'})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        color: 'white',
        textAlign: 'center',
        paddingTop: '5rem',
    };

    return (
        <div style={backgroundStyle}>
            <h1>What kind of movie are you in the mood for?</h1>
            <div className="button-container">
                <button onClick={() => handleMoodSelect('comedy')}>Lighthearted and Comedic</button>
                <button onClick={() => handleMoodSelect('drama')}>Serious and Dramatic</button>
            </div>
        </div>
    );
}

export default MoodSelectionPage;
