import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const MusicalQuestions = () => {
    const { answers, setAnswers } = useContext(AnswerContext);
    const navigate = useNavigate();

    const handleAnswer = (value) => {
        setAnswers(prev => ({ ...prev, Q2: value }));

        if (value === 0) {
            navigate('/animated');
        } else {
            navigate('/recommend/musical');
        }
    };

    return (
        <div style={styles.page}>
            <div style={styles.imageContainer}>
                <img
                    src="/images/musical.jpg"
                    alt="Musical theme"
                    style={styles.image}
                />
                <div style={styles.overlay}>
                    <h2 style={styles.question}>ðŸŽ¤ Do you prefer movies with a lot of musical numbers?</h2>
                </div>
            </div>

            <div style={styles.buttonContainer}>
                <button onClick={() => handleAnswer(1)} style={styles.button}>Yes</button>
                <button onClick={() => handleAnswer(0)} style={styles.button}>No</button>
            </div>
        </div>
    );

};

const styles = {
    page: {
        textAlign: 'center',
        padding: '0',
        margin: '0',
        fontFamily: 'Arial, sans-serif',
        backgroundColor: '#f8f8f8'
    },
    imageContainer: {
        position: 'relative',
        width: '100%',
        maxHeight: '60vh',
        overflow: 'hidden',
    },
    image: {
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        borderBottom: '4px solid #000'
    },
    overlay: {
        position: 'absolute',
        bottom: '10%',
        left: '50%',
        transform: 'translateX(-50%)',
        color: '#fff',
        backgroundColor: 'rgba(0, 0, 0, 0.6)',
        padding: '1rem 2rem',
        borderRadius: '12px',
        boxShadow: '0 4px 12px rgba(0,0,0,0.4)',
    },
    question: {
        margin: 0,
        fontSize: '1.5rem',
        fontWeight: 'bold'
    },
    buttonContainer: {
        marginTop: '2rem',
        display: 'flex',
        justifyContent: 'center',
        gap: '2rem'
    },
    button: {
        padding: '0.8rem 1.8rem',
        fontSize: '1.1rem',
        border: 'none',
        borderRadius: '8px',
        backgroundColor: '#D32F2F',
        color: '#fff',
        cursor: 'pointer',
        boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
        transition: 'transform 0.2s ease-in-out'
    }
};


export default MusicalQuestions;
