import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';
import './StorytellingQuestions.css';

const StorytellingQuestions = () => {
    const { answers, setAnswers } = useContext(AnswerContext);
    const navigate = useNavigate();

    const handleAnswer = (value) => {
        const updatedAnswers = { ...answers, Q5: value };
        setAnswers(updatedAnswers);
        navigate(value === 1 ? '/realism' : '/sports');
    };

    return (
        <div className="question-container">
            <h1>Is strong storytelling more important than character development for you when it comes to a movie?</h1>
            <button onClick={() => handleAnswer(1)}>Yes</button>
            <button onClick={() => handleAnswer(0)}>No</button>
        </div>
    );
};

export default StorytellingQuestions;
