import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const MusicalQuestions = () => {
    const { answers, setAnswers } = useContext(AnswerContext);
    const navigate = useNavigate();

    const handleAnswer = (value) => {
        setAnswers(prev => ({ ...prev, Q2: value }));

        if (value === 0) {
            navigate('/animated'); // No musical → ask about animation
        } else {
            navigate('/recommend/musical'); // Yes → go to musical recommendation
        }
    };

    return (
        <div style={styles.container}>
            <h2>Do you prefer movies with a lot of musical numbers?</h2>
            <div style={styles.buttonContainer}>
                <button onClick={() => handleAnswer(1)} style={styles.button}>Yes</button>
                <button onClick={() => handleAnswer(0)} style={styles.button}>No</button>
            </div>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '2rem',
        padding: '1rem'
    },
    buttonContainer: {
        marginTop: '1.5rem',
        display: 'flex',
        justifyContent: 'center',
        gap: '1rem'
    },
    button: {
        padding: '0.75rem 1.5rem',
        fontSize: '1.1rem',
        cursor: 'pointer'
    }
};

export default MusicalQuestions;
