import React, { useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CurtainContext } from '../CurtainContext';
import { motion, AnimatePresence } from 'framer-motion';

const CurtainOverlay = () => {
    const {
        showCurtain,
        setShowCurtain,
        nextRoute,
        curtainDuration
    } = useContext(CurtainContext);

    const navigate = useNavigate();

    useEffect(() => {
        if (showCurtain) {
            const timeout = setTimeout(() => {
                setShowCurtain(false);
                navigate(nextRoute);
            }, curtainDuration * 1000); // convert to ms

            return () => clearTimeout(timeout);
        }
    }, [showCurtain, curtainDuration, navigate, nextRoute, setShowCurtain]);

    return (
        <AnimatePresence>
            {showCurtain && (
                <>
                    <motion.div
                        className="curtain curtain-left"
                        initial={{ x: 0 }}
                        animate={{ x: '-100%' }}
                        exit={{ x: '-100%' }}
                        transition={{ duration: curtainDuration, ease: 'easeInOut' }}
                    />
                    <motion.div
                        className="curtain curtain-right"
                        initial={{ x: 0 }}
                        animate={{ x: '100%' }}
                        exit={{ x: '100%' }}
                        transition={{ duration: curtainDuration, ease: 'easeInOut' }}
                    />
                </>
            )}
        </AnimatePresence>
    );
};

export default CurtainOverlay;
