import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const MusicalRecommendation = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const movie = location.state?.movie;

    if (!movie) {
        return (
            <div style={styles.container}>
                <h2>No recommendation available.</h2>
                <button onClick={() => navigate('/')}>Go back</button>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h1>ðŸŽ¬ Your Musical Movie Recommendation</h1>
            <h2>{movie.title} ({movie.year})</h2>
            <p><strong>Rating:</strong> {movie.rating}</p>
            <p><strong>Duration:</strong> {movie.duration} minutes</p>
            <p><strong>Language:</strong> {movie.language}</p>
            <p><strong>Description:</strong> {movie.description}</p>
            {movie.poster && (
                <img
                    src={movie.poster}
                    alt={`${movie.title} poster`}
                    style={styles.poster}
                />
            )}
            <button onClick={() => navigate('/')} style={styles.button}>Choose Another Movie</button>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        padding: '2rem',
    },
    poster: {
        marginTop: '1rem',
        maxWidth: '300px',
        borderRadius: '8px',
    },
    button: {
        marginTop: '2rem',
        padding: '0.75rem 1.5rem',
        fontSize: '1rem',
        cursor: 'pointer',
    },
};

export default MusicalRecommendation;
