import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

const AnimatedQuestions = () => {
    const { answers, setAnswers } = useContext(AnswerContext);
    const navigate = useNavigate();

    const handleAnswer = async (value) => {
        const updatedAnswers = { ...answers, Q2: value };
        setAnswers(updatedAnswers);

        if (value === 1) {
            // YES → Recommend Animation
            try {
                const response = await fetch('http://localhost:5000/api/recommend', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ answers: updatedAnswers, filters: {} })
                });

                const data = await response.json();

                if (data.movie) {
                    navigate('/recommend/animation', { state: { movie: data.movie } });
                } else {
                    alert(data.message || 'No recommendation found.');
                }
            } catch (error) {
                console.error(error);
                alert('Error fetching recommendation.');
            }
        } else {
            // NO → Go to Romance question
            navigate('/romance');
        }
    };

    return (
        <div style={styles.container}>
            <h2>Do you enjoy movies that are animated?</h2>
            <div style={styles.buttonContainer}>
                <button onClick={() => handleAnswer(1)} style={styles.button}>Yes</button>
                <button onClick={() => handleAnswer(0)} style={styles.button}>No</button>
            </div>
        </div>
    );
};

const styles = {
    container: {
        textAlign: 'center',
        marginTop: '4rem',
    },
    buttonContainer: {
        marginTop: '2rem',
        display: 'flex',
        justifyContent: 'center',
        gap: '1.5rem',
    },
    button: {
        padding: '1rem 2rem',
        fontSize: '1.1rem',
        cursor: 'pointer',
    }
};

export default AnimatedQuestions;
