// src/pages/SeriousFlow/SportsQuestions.js

import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AnswerContext } from '../../AnswerContext';

function SportsQuestions() {
    const { answers, setAnswers } = useContext(AnswerContext);
    const navigate = useNavigate();

    const handleAnswer = (value) => {
        setAnswers(prev => ({ ...prev, Q9: value }));
        navigate('/flashback'); // Q10 comes next
    };

    return (
        <div style={styles.container}>
            <h1>Do you prefer movies that center around sports and athletic competitions over movies that explore other types of activities and hobbies?</h1>
            <div style={styles.buttonContainer}>
                <button onClick={() => handleAnswer(1)} style={styles.button}>Yes</button>
                <button onClick={() => handleAnswer(0)} style={styles.button}>No</button>
            </div>
        </div>
    );
}

const styles = {
    container: {
        textAlign: 'center',
        padding: '2rem'
    },
    buttonContainer: {
        marginTop: '2rem'
    },
    button: {
        margin: '0 1rem',
        padding: '0.75rem 1.5rem',
        fontSize: '1rem',
        cursor: 'pointer'
    }
};

export default SportsQuestions;
